package codingmentor.java.buoi5homework;

public class SchoolYear {
	private int id;
	private String name;
	
	public SchoolYear() {
		// TODO Auto-generated constructor stub
	}

	public SchoolYear(int id, String name) {
		super();
		this.id = id;
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
}
